mapper.select:
SELECT p.id as storeId , p.tel as transferTel ,s.tel AS lookTel ,p.contacts AS transfer , s.contacts AS looker ,t.sys_user AS sysUser ,(SELECT city_name FROM bs_city WHERE city_id = p.city_id) as city , t.success_time as successTime FROM success_case t JOIN transfer_shop p on p.id=t.transfer_shop_id JOIN look_shop s on s.id = t.look_shop_id where 5 = 5
and p.contacts like '%${transfer}%'
and s.contacts like '%${looker}%'
and p.tel = #{transferTel,jdbcType=VARCHAR}
and s.tel = #{lookTel,jdbcType=VARCHAR}
order by t.success_time desc
